contracts/BaseReward.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "hardhat/console.sol";
import "./interfaces/IReward.sol";

/**
 * @title BaseReward
 * @notice Base implementation for reward contracts
 * @dev Abstract contract with common functionality for all reward types
 */
abstract contract BaseReward is IReward, Ownable, ReentrancyGuard {
    // Claim tracking
    mapping(address => bool) public hasClaimed;
    uint256 public totalClaims;

    // Claim period
    uint256 public claimStartDate;
    uint256 public claimFinishDate;

    // Status flags
    bool public active;
    bool internal _initialized;

    // Events
    event Claimed(address indexed user, uint256 timestamp);
    event ClaimPeriodUpdated(uint256 startDate, uint256 finishDate);
    event StatusChanged(bool active);
    event ControllerUpdated(address controller);

    // Modifier to prevent reinitialization
    modifier initializer() {
        console.log("BaseReward: Initializer called, initialized status:", _initialized);
        require(!_initialized, "Already initialized");
        _;
        _initialized = true;
        console.log("BaseReward: Contract initialized");
    }

    function isInitialized() public view returns (bool) {
        return _initialized;
    }

    // Modifier to restrict actions to the active period
    modifier whenActive() {
        console.log("BaseReward: whenActive check");
        console.log("BaseReward: Active status:", active);
        console.log("BaseReward: Current timestamp:", block.timestamp);
        console.log("BaseReward: Claim start date:", claimStartDate);
        console.log("BaseReward: Claim finish date:", claimFinishDate);
        
        require(active, "Reward is not active");
        
        bool withinTime = block.timestamp >= claimStartDate &&
                (claimFinishDate == 0 || block.timestamp <= claimFinishDate);
                
        console.log("BaseReward: Within time window:", withinTime);
        
        require(
            withinTime,
            "Outside claim period"
        );
        _;
    }

    /**
     * @notice Constructor disables initialization for the implementation contract
     */
    constructor() {
        console.log("BaseReward: Constructor called");
        _initialized = true;
        _transferOwnership(msg.sender); // Set the owner manually
        console.log("BaseReward: Implementation contract locked");
    }

    /**
     * @notice Initialize the reward contract as a clone
     * @param config Configuration data for the reward
     * @param owner Address of the contract owner
     */
    function initializeClone(
        bytes memory config,
        address owner,
        address controller
    ) external override initializer {
        console.log("BaseReward: Initializing clone with owner:", owner);
        console.log("BaseReward: Controller:", controller);
        
        require(owner != address(0), "Invalid owner address");
        _transferOwnership(owner);
        _initialize(config);
        _setController(controller); // New method
        
        console.log("BaseReward: Clone initialization complete");
    }

    function _initialize(bytes memory config) internal virtual {
        console.log("BaseReward: Internal initialization");
        require(validateConfig(config), "Invalid configuration");
        // Specific initialization logic to be implemented by derived contracts
    }

    /**
     * @notice Set claim period
     * @param startDate Start date of the claim period (timestamp)
     * @param finishDate End date of the claim period (timestamp, 0 for no end)
     */
    function setClaimPeriod(
        uint256 startDate,
        uint256 finishDate
    ) external onlyOwner {
        require(
            startDate <= finishDate || finishDate == 0,
            "Invalid claim period"
        );
        console.log("BaseReward: Setting claim period");
        console.log("BaseReward: Start date:", startDate);
        console.log("BaseReward: Finish date:", finishDate);
        
        claimStartDate = startDate;
        claimFinishDate = finishDate;
        emit ClaimPeriodUpdated(startDate, finishDate);
        
        console.log("BaseReward: Claim period updated");
    }

    /**
     * @notice Extend the claim period
     * @param extension Duration in seconds to extend the claim period
     */
    function extendClaimPeriod(uint256 extension) external onlyOwner {
        require(claimFinishDate > 0, "No finish date set");
        console.log("BaseReward: Extending claim period by", extension, "seconds");
        console.log("BaseReward: Original finish date:", claimFinishDate);
        
        claimFinishDate += extension;
        emit ClaimPeriodUpdated(claimStartDate, claimFinishDate);
        
        console.log("BaseReward: New finish date:", claimFinishDate);
    }

    /**
     * @notice Activate the reward
     */
    function activate() external onlyOwner {
        console.log("BaseReward: Activating reward");
        active = true;
        emit StatusChanged(true);
        console.log("BaseReward: Reward activated");
    }

    /**
     * @notice Deactivate the reward
     */
    function deactivate() external onlyOwner {
        console.log("BaseReward: Deactivating reward");
        active = false;
        emit StatusChanged(false);
        console.log("BaseReward: Reward deactivated");
    }

    /**
     * @notice Check if the claim period is active
     * @return Whether the claim period is active
     */
    function isClaimPeriodActive() external view returns (bool) {
        bool isActive = active &&
            block.timestamp >= claimStartDate &&
            (claimFinishDate == 0 || block.timestamp <= claimFinishDate);
            
        return isActive;
    }

    /**
     * @notice Process fee on claim (base implementation)
     * @param user Address of the user claiming
     * @return Whether fee processing was successful
     */
    function processFeeOnClaim(
        address user
    ) external virtual override returns (bool) {
        console.log("BaseReward: Processing fee for user", user);
        // Base implementation simply returns true
        // Derived contracts can override with fee handling logic
        return true;
    }

    /**
     * @notice Process fee with discount (base implementation)
     * @param user Address of the user claiming
     * @param discountRate Discount rate in basis points
     * @param merkleProof Merkle proof for the discount
     * @return Whether fee processing was successful
     */
    function processFeeWithDiscount(
        address user,
        uint256 discountRate,
        bytes32[] calldata merkleProof
    ) external virtual override returns (bool) {
        console.log("BaseReward: Processing fee with discount for user", user);
        console.log("BaseReward: Discount rate:", discountRate);
        // Base implementation simply returns true
        // Derived contracts can override with fee handling logic
        return true;
    }

    /**
     * @notice Check if a user can claim a reward
     * @param user Address of the user to check
     * @return Whether the user can claim
     */
    function canClaim(
        address user
    ) public view virtual override returns (bool) {
        bool activeStatus = active;
        bool hasClaimedBefore = hasClaimed[user];
        bool withinStartTime = block.timestamp >= claimStartDate;
        bool withinEndTime = claimFinishDate == 0 || block.timestamp <= claimFinishDate;
        
        // Don't log in view functions as they might be called internally
        // This is a special case since we're debugging
        if (msg.sender == address(0)) {
            console.log("BaseReward: Checking if user", user, "can claim");
            console.log("BaseReward: Active?", activeStatus);
            console.log("BaseReward: Has claimed?", hasClaimedBefore);
            console.log("BaseReward: After start time?", withinStartTime);
            console.log("BaseReward: Before end time?", withinEndTime);
        }
        
        bool result = activeStatus &&
            !hasClaimedBefore &&
            withinStartTime &&
            withinEndTime;
            
        // Don't log in view functions as they might be called internally
        if (msg.sender == address(0)) {
            console.log("BaseReward: Can claim result:", result);
        }
        
        return result;
    }

    /**
     * @dev Internal function to track a claim
     * @param user Address of the user claiming
     */
    function _trackClaim(address user) internal {
        console.log("BaseReward: Tracking claim for user", user);
        hasClaimed[user] = true;
        totalClaims++;
        emit Claimed(user, block.timestamp);
        console.log("BaseReward: Claim tracked successfully, total claims:", totalClaims);
    }

    /**
     * @notice Validate configuration data
     * @param config Configuration data to validate
     * @return Whether the configuration is valid
     */
    function validateConfig(
        bytes memory config
    ) public pure virtual override returns (bool);

    function _setController(address controller) internal virtual {
        // Base implementation doesn't do anything
        // Derived contracts will override this
        console.log("BaseReward: Setting controller address (base implementation)");
        emit ControllerUpdated(controller);
    }
}

contracts/interfaces/IReward.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title IReward
 * @notice Interface for reward contracts in the Tim3cap platform
 * @dev Defines the required methods for all reward implementations
 */
interface IReward {
    /**
     * @notice Initialize the reward contract
     * @param config Configuration data for the reward
     */
    function initialize(bytes memory config) external;

/**
 * @notice Initialize the reward contract as a clone
 * @param config Configuration data for the reward
 * @param owner Address of the contract owner
 * @param controller Address of the controller (Tim3cap core)
 */
function initializeClone(bytes memory config, address owner, address controller) external;

    /**
     * @notice Claim a reward for a user
     * @param user Address of the user to receive the reward
     */
    function claim(address user) external;

    /**
     * @notice Check if a user can claim a reward
     * @param user Address of the user to check
     * @return Whether the user can claim
     */
    function canClaim(address user) external view returns (bool);

    /**
     * @notice Process fee on claim
     * @param user Address of the user claiming
     * @return Whether fee processing was successful
     */
    function processFeeOnClaim(address user) external returns (bool);

    /**
     * @notice Process fee with discount
     * @param user Address of the user claiming
     * @param discountRate Discount rate in basis points
     * @param merkleProof Merkle proof for the discount
     * @return Whether fee processing was successful
     */
    function processFeeWithDiscount(
        address user,
        uint256 discountRate,
        bytes32[] calldata merkleProof
    ) external returns (bool);

    /**
     * @notice Get the reward type identifier
     * @return Type identifier for the reward
     */
    function getRewardType() external pure returns (string memory);

    /**
     * @notice Validate configuration data
     * @param config Configuration data to validate
     * @return Whether the configuration is valid
     */
    function validateConfig(bytes memory config) external pure returns (bool);
    
}

contracts/rewards/WhitelistRaffleReward..content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "../BaseReward.sol";

/**
 * @title WhitelistRaffleReward
 * @notice Reward implementation for raffled whitelist spot distributions
 * @dev Records winners and supports both manual assignments and automatic distribution
 */
contract WhitelistRaffleReward is BaseReward {
    
    // Config structure for whitelist raffle
    struct Config {
        string raffleName;           // Name of the raffle
        uint256 spotsCount;          // Total number of whitelist spots
        uint256 winnersCount;        // Expected number of winners
        bool automaticDistribution;  // Whether to distribute automatically
        uint256 distributionDate;    // Date for automatic distribution (if enabled)
    }
    
    // Config instance
    Config public config;
    
    // Add controller variable to allow the Tim3cap core contract to call claim
    address public controller;
    
    // Winner management
    mapping(address => bool) public isWinner;
    mapping(address => bool) public isAssigned;
    uint256 public totalWinners;
    uint256 public totalAssigned;
    
    // Events
    event WinnerAdded(address indexed winner, uint256 timestamp);
    event WinnerRemoved(address indexed winner, uint256 timestamp);
    event SpotAssigned(address indexed winner, uint256 timestamp);
    event AutomaticDistributionEnabled(uint256 distributionDate);
    event AutomaticDistributionDisabled();
    
    /**
     * @notice Initialize the reward contract
     * @param configData Configuration data for the reward
     */
    function initialize(bytes memory configData) external override initializer {
        _initialize(configData);
    }

    /**
     * @dev Internal initialization function for WhitelistRaffleReward
     * @param configData Configuration data for the reward
     */
    function _initialize(bytes memory configData) internal override {
        // Decode config data
        (
            string memory _raffleName,
            uint256 _spotsCount,
            uint256 _winnersCount,
            bool _automaticDistribution,
            uint256 _distributionDate
        ) = abi.decode(
            configData,
            (string, uint256, uint256, bool, uint256)
        );
        
        require(bytes(_raffleName).length > 0, "Invalid raffle name");
        require(_spotsCount > 0, "Invalid spots count");
        
        // If automatic distribution is enabled, require a valid distribution date
        if (_automaticDistribution) {
            require(_distributionDate > block.timestamp, "Distribution date must be in the future");
        }
        
        // Store config values
        config = Config({
            raffleName: _raffleName,
            spotsCount: _spotsCount,
            winnersCount: _winnersCount,
            automaticDistribution: _automaticDistribution,
            distributionDate: _distributionDate
        });
        
        // Set initial state
        active = true;
        claimStartDate = block.timestamp;
        
        // Set claim finish date based on distribution mode
        if (_automaticDistribution) {
            claimFinishDate = _distributionDate;
        } else {
            claimFinishDate = block.timestamp + 30 days; // Default 30 day claim period
        }
        
        // Set the contract that initialized this reward as the controller
        // This will be the Tim3cap core contract during deployment
        controller = msg.sender;
    }
    
    /**
     * @notice Get the reward type identifier
     * @return Type identifier "WHITELIST_RAFFLE"
     */
    function getRewardType() public pure override returns (string memory) {
        return "WHITELIST_RAFFLE";
    }
    
    /**
     * @notice Validate configuration data
     * @param configData Configuration data to validate
     * @return Whether the configuration is valid
     */
    function validateConfig(bytes memory configData) public pure override returns (bool) {
        if (configData.length < 5 * 32) {
            return false;
        }
        
        return true;
    }
    
    /**
     * @notice External assign function for a winner
     */
    function assignSpot() external nonReentrant whenActive {
        claim(msg.sender);
    }
    
    /**
     * @notice Claim function called by Tim3cap contract
     * @param user Address to assign whitelist spot
     */
    function claim(address user) public override nonReentrant whenActive {
        if (msg.sender != user) {
            // Allow either the owner or the controller (Tim3cap core) to call this function
            require(msg.sender == owner() || msg.sender == controller, "Not authorized");
        }
        
        require(canClaim(user), "Cannot claim spot");
        require(isWinner[user], "Not a winner");
        require(!hasClaimed[user], "Already claimed");
        
        // Mark as claimed - use inherited hasClaimed mapping and totalClaims counter
        hasClaimed[user] = true;
        totalClaims++;
        
        // Mark as assigned
        _assignSpot(user);
        
        // Record claim for analytics
        _trackClaim(user);
    }
    
    /**
     * @notice Trigger automatic distribution for a batch of winners
     * @dev Can only be called after distribution date and only if automatic distribution is enabled
     * @param winners Array of winner addresses to process in this batch
     */
    function triggerAutomaticDistribution(address[] calldata winners) external nonReentrant {
        require(msg.sender == owner() || msg.sender == controller, "Not authorized");
        require(config.automaticDistribution, "Automatic distribution not enabled");
        require(block.timestamp >= config.distributionDate, "Distribution date not reached");
        
        for (uint256 i = 0; i < winners.length; i++) {
            address winner = winners[i];
            
            // Verify this is actually a winner
            if (!isWinner[winner]) continue;
            
            // Skip winners who already claimed
            if (hasClaimed[winner]) continue;
            
            // Mark as claimed
            hasClaimed[winner] = true;
            totalClaims++;
            
            // Mark as assigned
            _assignSpot(winner);
            
            // Record claim for analytics
            _trackClaim(winner);
        }
    }
    
    /**
     * @dev Internal function to assign whitelist spot to a winner
     * @param winner Address of the winner to receive whitelist spot
     */
    function _assignSpot(address winner) internal {
        // Check if not already assigned
        if (!isAssigned[winner]) {
            isAssigned[winner] = true;
            totalAssigned++;
            
            emit SpotAssigned(winner, block.timestamp);
        }
    }
    
    /**
     * @notice Check if a user can claim the reward
     * @param user Address of the user to check
     * @return Whether the user can claim
     */
    function canClaim(address user) public view override returns (bool) {
        if (!active) return false;
        if (hasClaimed[user]) return false;
        if (!isWinner[user]) return false;
        
        // In automatic distribution mode, users can only claim before distribution date
        if (config.automaticDistribution && block.timestamp >= config.distributionDate) {
            return false;
        }
        
        // Check time window
        if (block.timestamp < claimStartDate || (claimFinishDate > 0 && block.timestamp > claimFinishDate)) {
            return false;
        }
        
        return true;
    }
    
    /**
     * @notice Process fee on claim - implementation for IReward
     * @param user Address that is claiming
     * @return Always true as no fees are charged
     */
    function processFeeOnClaim(address user) external override returns (bool) {
        return true; // No fees for whitelist raffle
    }
    
    /**
     * @notice Process fee with discount - implementation for IReward
     * @param user Address that is claiming
     * @param discountRate Discount rate in basis points
     * @param merkleProof Merkle proof for the discount
     * @return Always true as no fees are charged
     */
    function processFeeWithDiscount(
        address user,
        uint256 discountRate,
        bytes32[] calldata merkleProof
    ) external override returns (bool) {
        return true; // No fees for whitelist raffle
    }
    
    /**
     * @notice Set raffle winners (only owner)
     * @param winners Array of winning addresses
     */
    function setWinners(address[] calldata winners) external onlyOwner {
        require(winners.length > 0, "No winners provided");
        require(winners.length <= config.spotsCount, "Too many winners");
        
        // Reset all previous winners by resetting totalWinners
        // Note: Individual isWinner mappings for old winners will remain true, but this is 
        // acceptable as they won't be counted in totalWinners and won't affect functionality
        totalWinners = 0;
        
        // Set new winners
        for (uint256 i = 0; i < winners.length; i++) {
            address winner = winners[i];
            require(winner != address(0), "Invalid winner address");
            
            // Only increment totalWinners if this is a new winner
            if (!isWinner[winner]) {
                isWinner[winner] = true;
                totalWinners++;
            }
            
            emit WinnerAdded(winner, block.timestamp);
        }
    }
    
    /**
     * @notice Add a single winner
     * @param winner Address of the winner to add
     */
    function addWinner(address winner) external onlyOwner {
        require(winner != address(0), "Invalid winner address");
        require(!isWinner[winner], "Already a winner");
        require(totalWinners < config.spotsCount, "All spots allocated");
        
        isWinner[winner] = true;
        totalWinners++;
        
        emit WinnerAdded(winner, block.timestamp);
    }
    
    /**
     * @notice Remove a single winner
     * @param winner Address of the winner to remove
     */
    function removeWinner(address winner) external onlyOwner {
        require(isWinner[winner], "Not a winner");
        require(!hasClaimed[winner], "Winner has already claimed");
        
        isWinner[winner] = false;
        totalWinners--;
        
        emit WinnerRemoved(winner, block.timestamp);
    }
    
    /**
     * @notice Toggle automatic distribution mode
     * @param enable Whether to enable automatic distribution
     * @param newDistributionDate New distribution date (if enabling)
     */
    function setAutomaticDistribution(bool enable, uint256 newDistributionDate) external onlyOwner {
        if (enable) {
            require(newDistributionDate > block.timestamp, "Distribution date must be in the future");
            config.automaticDistribution = true;
            config.distributionDate = newDistributionDate;
            claimFinishDate = newDistributionDate;
            
            emit AutomaticDistributionEnabled(newDistributionDate);
        } else {
            config.automaticDistribution = false;
            claimFinishDate = block.timestamp + 30 days; // Default 30 day claim period
            
            emit AutomaticDistributionDisabled();
        }
    }
    
    /**
     * @notice Set controller address
     * @param _controller New controller address
     */
    function setController(address _controller) external onlyOwner {
        require(_controller != address(0), "Invalid controller address");
        controller = _controller;
    }
    
    /**
     * @dev Internal function to set controller address
     * @param _controller New controller address
     */
    function _setController(address _controller) internal override {
        controller = _controller;
    }
    
    /**
     * @notice Get winner status
     * @dev Note: Full winner list is available through WinnerAdded events
     * @return Number of winners set for this raffle
     */
    function getWinnerCount() external view returns (uint256) {
        return totalWinners;
    }
    
    /**
     * @notice Get assignment status
     * @dev Note: Full assigned list is available through SpotAssigned events
     * @return Number of spots that have been assigned
     */
    function getAssignedCount() external view returns (uint256) {
        return totalAssigned;
    }
    
    /**
     * @notice Check if an address is a winner and if they've claimed
     * @param user Address to check
     * @return isAWinner Whether the address is a winner
     * @return hasClaimed Whether the address has claimed their spot
     */
    function checkWinnerStatus(address user) external view returns (bool, bool) {
        return (isWinner[user], hasClaimed[user]);
    }
    
    /**
     * @notice Check if an address has been assigned a whitelist spot
     * @param user Address to check
     * @return Whether the address has been assigned a spot
     */
    function checkAssignmentStatus(address user) external view returns (bool) {
        return isAssigned[user];
    }
    
    /**
     * @notice Get raffle stats
     * @return raffleName Name of the raffle
     * @return spotsCount Total number of whitelist spots
     * @return winnersCount Total number of winners
     * @return assignedCount Number of assigned spots
     * @return totalWinnersClaimed Number of winners who have claimed
     * @return isAutomatic Whether automatic distribution is enabled
     * @return distributionDate Date for automatic distribution (if enabled)
     */
    function getRaffleStats() external view returns (
        string memory raffleName,
        uint256 spotsCount,
        uint256 winnersCount,
        uint256 assignedCount,
        uint256 totalWinnersClaimed,
        bool isAutomatic,
        uint256 distributionDate
    ) {
        return (
            config.raffleName,
            config.spotsCount,
            totalWinners,
            totalAssigned,
            totalClaims,
            config.automaticDistribution,
            config.distributionDate
        );
    }
}

contracts/rewards/WhitelistSpots.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "../BaseReward.sol";

/**
 * @title WhitelistSpots
 * @notice Reward implementation for whitelist spot assignments to eligible users
 * @dev Records eligibility and supports both manual claims and automatic distribution
 */
contract WhitelistSpots is BaseReward {
    
    // Config structure for whitelist
    struct Config {
        string whitelistName;        // Name of the whitelist
        bool automaticDistribution;  // Whether to distribute automatically
        uint256 distributionDate;    // Date for automatic distribution (if enabled)
    }
    
    // Config instance
    Config public config;
    
    // Add controller variable to allow the Tim3cap core contract to call claim
    address public controller;
    
    // Eligibility management
    mapping(address => bool) public isEligible;
    mapping(address => bool) public isAssigned;
    uint256 public totalEligible;
    uint256 public totalAssigned;
    
    // Events
    event EligibleBatch(address[] users, uint256 timestamp);
    event EligibleSingle(address indexed user, uint256 timestamp);
    event EligibleRemoved(address indexed user, uint256 timestamp);
    event SpotAssigned(address indexed user, uint256 timestamp);
    event AutomaticDistributionEnabled(uint256 distributionDate);
    event AutomaticDistributionDisabled();
    
    /**
     * @notice Initialize the reward contract
     * @param configData Configuration data for the reward
     */
    function initialize(bytes memory configData) external override initializer {
        _initialize(configData);
    }

    /**
     * @dev Internal initialization function for WhitelistSpots
     * @param configData Configuration data for the reward
     */
    function _initialize(bytes memory configData) internal override {
        // Decode config data
        (
            string memory _whitelistName,
            bool _automaticDistribution,
            uint256 _distributionDate
        ) = abi.decode(
            configData,
            (string, bool, uint256)
        );
        
        require(bytes(_whitelistName).length > 0, "Invalid whitelist name");
        
        // If automatic distribution is enabled, require a valid distribution date
        if (_automaticDistribution) {
            require(_distributionDate > block.timestamp, "Distribution date must be in the future");
        }
        
        // Store config values
        config = Config({
            whitelistName: _whitelistName,
            automaticDistribution: _automaticDistribution,
            distributionDate: _distributionDate
        });
        
        // Set initial state
        active = true;
        claimStartDate = block.timestamp;
        
        // Set claim finish date based on distribution mode
        if (_automaticDistribution) {
            claimFinishDate = _distributionDate;
        } else {
            claimFinishDate = block.timestamp + 30 days; // Default 30 day claim period
        }
        
        // Set the contract that initialized this reward as the controller
        // This will be the Tim3cap core contract during deployment
        controller = msg.sender;
    }
    
    /**
     * @notice Get the reward type identifier
     * @return Type identifier "WHITELIST_SPOTS"
     */
    function getRewardType() public pure override returns (string memory) {
        return "WHITELIST_SPOTS";
    }
    
    /**
     * @notice Validate configuration data
     * @param configData Configuration data to validate
     * @return Whether the configuration is valid
     */
    function validateConfig(bytes memory configData) public pure override returns (bool) {
        if (configData.length < 3 * 32) {
            return false;
        }
        
        return true;
    }
    
    /**
     * @notice External assign function for an eligible user
     */
    function assignSpot() external nonReentrant whenActive {
        claim(msg.sender);
    }
    
    /**
     * @notice Claim function called by Tim3cap contract
     * @param user Address to assign whitelist spot
     */
    function claim(address user) public override nonReentrant whenActive {
        if (msg.sender != user) {
            // Allow either the owner or the controller (Tim3cap core) to call this function
            require(msg.sender == owner() || msg.sender == controller, "Not authorized");
        }
        
        require(canClaim(user), "Cannot claim spot");
        require(isEligible[user], "Not eligible");
        require(!hasClaimed[user], "Already claimed");
        
        // Mark as claimed - use inherited hasClaimed mapping and totalClaims counter
        hasClaimed[user] = true;
        totalClaims++;
        
        // Mark as assigned
        _assignSpot(user);
        
        // Record claim for analytics
        _trackClaim(user);
    }
    
    /**
     * @notice Trigger automatic distribution for a batch of eligible users
     * @dev Can only be called after distribution date and only if automatic distribution is enabled
     * @param users Array of eligible user addresses to process in this batch
     */
    function triggerAutomaticDistribution(address[] calldata users) external nonReentrant {
        require(msg.sender == owner() || msg.sender == controller, "Not authorized");
        require(config.automaticDistribution, "Automatic distribution not enabled");
        require(block.timestamp >= config.distributionDate, "Distribution date not reached");
        
        for (uint256 i = 0; i < users.length; i++) {
            address user = users[i];
            
            // Verify this is actually eligible
            if (!isEligible[user]) continue;
            
            // Skip users who already claimed
            if (hasClaimed[user]) continue;
            
            // Mark as claimed
            hasClaimed[user] = true;
            totalClaims++;
            
            // Mark as assigned
            _assignSpot(user);
            
            // Record claim for analytics
            _trackClaim(user);
        }
    }
    
    /**
     * @dev Internal function to assign whitelist spot to an eligible user
     * @param user Address of the user to receive whitelist spot
     */
    function _assignSpot(address user) internal {
        // Check if not already assigned
        if (!isAssigned[user]) {
            isAssigned[user] = true;
            totalAssigned++;
            
            emit SpotAssigned(user, block.timestamp);
        }
    }
    
    /**
     * @notice Check if a user can claim the reward
     * @param user Address of the user to check
     * @return Whether the user can claim
     */
    function canClaim(address user) public view override returns (bool) {
        if (!active) return false;
        if (hasClaimed[user]) return false;
        if (!isEligible[user]) return false;
        
        // In automatic distribution mode, users can only claim before distribution date
        if (config.automaticDistribution && block.timestamp >= config.distributionDate) {
            return false;
        }
        
        // Check time window
        if (block.timestamp < claimStartDate || (claimFinishDate > 0 && block.timestamp > claimFinishDate)) {
            return false;
        }
        
        return true;
    }
    
    /**
     * @notice Process fee on claim - implementation for IReward
     * @param user Address that is claiming
     * @return Always true as no fees are charged
     */
    function processFeeOnClaim(address user) external override returns (bool) {
        return true; // No fees for whitelist spots
    }
    
    /**
     * @notice Process fee with discount - implementation for IReward
     * @param user Address that is claiming
     * @param discountRate Discount rate in basis points
     * @param merkleProof Merkle proof for the discount
     * @return Always true as no fees are charged
     */
    function processFeeWithDiscount(
        address user,
        uint256 discountRate,
        bytes32[] calldata merkleProof
    ) external override returns (bool) {
        return true; // No fees for whitelist spots
    }
    
    /**
     * @notice Set eligible users in batch (only owner)
     * @param users Array of eligible user addresses
     */
    function addEligibleBatch(address[] calldata users) external onlyOwner {
        require(users.length > 0, "No users provided");
        
        // Set new eligibility
        for (uint256 i = 0; i < users.length; i++) {
            address user = users[i];
            require(user != address(0), "Invalid user address");
            
            // Only increment counter if this is newly eligible
            if (!isEligible[user]) {
                isEligible[user] = true;
                totalEligible++;
            }
        }
        
        // Emit a single batch event instead of individual events
        emit EligibleBatch(users, block.timestamp);
    }
    
    /**
     * @notice Add a single eligible user
     * @param user Address of the user to add
     */
    function addEligible(address user) external onlyOwner {
        require(user != address(0), "Invalid user address");
        require(!isEligible[user], "Already eligible");
        
        isEligible[user] = true;
        totalEligible++;
        
        emit EligibleSingle(user, block.timestamp);
    }
    
    /**
     * @notice Remove eligibility for a user
     * @param user Address of the user to remove
     */
    function removeEligible(address user) external onlyOwner {
        require(isEligible[user], "Not eligible");
        require(!hasClaimed[user], "User has already claimed");
        
        isEligible[user] = false;
        totalEligible--;
        
        emit EligibleRemoved(user, block.timestamp);
    }
    
    /**
     * @notice Toggle automatic distribution mode
     * @param enable Whether to enable automatic distribution
     * @param newDistributionDate New distribution date (if enabling)
     */
    function setAutomaticDistribution(bool enable, uint256 newDistributionDate) external onlyOwner {
        if (enable) {
            require(newDistributionDate > block.timestamp, "Distribution date must be in the future");
            config.automaticDistribution = true;
            config.distributionDate = newDistributionDate;
            claimFinishDate = newDistributionDate;
            
            emit AutomaticDistributionEnabled(newDistributionDate);
        } else {
            config.automaticDistribution = false;
            claimFinishDate = block.timestamp + 30 days; // Default 30 day claim period
            
            emit AutomaticDistributionDisabled();
        }
    }
    
    /**
     * @notice Set controller address
     * @param _controller New controller address
     */
    function setController(address _controller) external onlyOwner {
        require(_controller != address(0), "Invalid controller address");
        controller = _controller;
    }
    
    /**
     * @dev Internal function to set controller address
     * @param _controller New controller address
     */
    function _setController(address _controller) internal override {
        controller = _controller;
    }
    
    /**
     * @notice Get eligibility stats
     * @return Number of users marked as eligible
     */
    function getEligibleCount() external view returns (uint256) {
        return totalEligible;
    }
    
    /**
     * @notice Get assignment stats
     * @return Number of spots that have been assigned
     */
    function getAssignedCount() external view returns (uint256) {
        return totalAssigned;
    }
    
    /**
     * @notice Check if an address is eligible and if they've claimed
     * @param user Address to check
     * @return isUserEligible Whether the address is eligible
     * @return hasUserClaimed Whether the address has claimed their spot
     */
    function checkEligibilityStatus(address user) external view returns (bool, bool) {
        return (isEligible[user], hasClaimed[user]);
    }
    
    /**
     * @notice Check if an address has been assigned a whitelist spot
     * @param user Address to check
     * @return Whether the address has been assigned a spot
     */
    function checkAssignmentStatus(address user) external view returns (bool) {
        return isAssigned[user];
    }
    
    /**
     * @notice Get whitelist stats
     * @return whitelistName Name of the whitelist
     * @return eligibleCount Total number of eligible users
     * @return assignedCount Number of assigned spots
     * @return totalClaimsCount Number of users who have claimed
     * @return isAutomatic Whether automatic distribution is enabled
     * @return distributionDate Date for automatic distribution (if enabled)
     */
    function getWhitelistStats() external view returns (
        string memory whitelistName,
        uint256 eligibleCount,
        uint256 assignedCount,
        uint256 totalClaimsCount,
        bool isAutomatic,
        uint256 distributionDate
    ) {
        return (
            config.whitelistName,
            totalEligible,
            totalAssigned,
            totalClaims,
            config.automaticDistribution,
            config.distributionDate
        );
    }
}

